mapper.select:
select 'false' as QUERYID,

from AUTO_TEST_SCENE_SET_REL
where id = #{id,jdbcType=BIGINT}

select *
from AUTO_TEST_SCENE_SET_REL
where isDelete = 0 and setId = #{setId}
order by updateTime asc
limit #{pageQry.offset}, #{pageQry.size}

select *
from AUTO_TEST_SCENE_SET_REL
where setId = #{setId} and type = #{type}

  and status = #{status}

and isDelete = 0

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into AUTO_TEST_SCENE_SET_REL (setId, stepId, sceneId, 
  sort, type, isDelete, 
  status, createTime, updateTime
  )
values (#{setId,jdbcType=BIGINT}, #{stepId,jdbcType=BIGINT}, #{sceneId,jdbcType=BIGINT},
  #{sort,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{isDelete,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}
  )
